mapper.select:
select id,
from t_resource


        and uuid = #{uuid}


        and status = #{status}


        and resource_name = #{resourceName}

mapper.insert:
insert into t_resource
(uuid
,is_deleted
,status
,comment
,create_user_uuid
,create_time
,resource_name
,resource_type
,resource_url)
values
(#{uuid}
,#{isDeleted}
,#{status}
,#{comment}
,#{createUserUuid}
,#{createTime}
,#{resourceName}
,#{resourceType}
,#{resourceUrl})
on duplicate key update
update_user_uuid = #{updateUserUuid}
,update_time = current_timestamp
